mapper.select:
select tug_fee.id, tug_fee.ship_name, tug_fee.length, tug_fee.depth, tug_fee.work_place, tug_fee.work_time, tug_fee.tug_num, tug_fee.tug_unit_price, tug_fee.amount, tug_fee.caculator_comment, tug_fee.reviewer_comment, tug_fee.applicant_comment, tug_fee.applicant_confirm_comment, tug_fee.applicate_time, tug_fee.admin_confirm_time, tug_fee.caculate_time, tug_fee.review_time, tug_fee.applicant_confirm_time, tug_fee.create_time, tug_fee.update_time, tug_fee.caculate_amount, tug_fee.applicant_id, tug_fee.reviewer_id, tug_fee.caculator_id, tug_fee.admin_id, tug_fee.state, tug_fee.ship_type, tug_fee.work_type from tug_fee where 1 = 1
and tug_fee.ship_name like concat('%',#{shipName},'%')
and tug_fee.ship_type = #{shipType}
and tug_fee.create_time >= #{startTime}
